import React, { useState } from "react";
import PageTitle from "../components/Typography/PageTitle";
import { Input, Label, Select } from "@windmill/react-ui";
import { Button } from "@windmill/react-ui";

function Forms() {
  const [academicYear, setAcademicYear] = useState(""); // State to manage selected academic year

  const handleAcademicYearChange = (e) => {
    setAcademicYear(e.target.value);
  };

  // Function to get semester options based on academic year
  const getSemesterOptions = () => {
    if (academicYear === "2021-2025") {
      return ["Semester 6", "Semester 7"];
    } else if (academicYear === "2022-2026") {
      return ["Semester 7", "Semester 8"];
    } else {
      return [];
    }
  };

  return (
    <>
      <PageTitle>Course Registration</PageTitle>
      <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">
        <Label>
          <span>Roll No</span>
          <Input className="mt-1" placeholder="7376211CS239" />
        </Label>
        <Label className="mt-4">
          <span>Name</span>
          <Input className="mt-1" placeholder="Poovarasan" />
        </Label>
        <div className="mt-4">
          <Label>Academic year</Label>
          <div className="mt-2">
            <Label radio>
              <Input
                type="radio"
                value="2021-2025"
                name="academicYear"
                checked={academicYear === "2021-2025"}
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2023-2024</span>
            </Label>
            <Label className="ml-6" radio>
              <Input
                type="radio"
                value="2022-2026"
                name="academicYear"
                checked={academicYear === "2022-2026"}
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2024-2025</span>
            </Label>
          </div>
        </div>

        {/* Semester selection based on academic year */}
        {academicYear && (
          <div className="mt-4">
            <Label>Semester</Label>
            <div className="mt-2">
              {getSemesterOptions().map((semester, index) => (
                <Label key={index} radio className="mr-6">
                  <Input type="radio" value={semester} name="semester" onChange={handleAcademicSemesterChange} />
                  <span className="ml-2">{semester}</span>
                </Label>
              ))}
            </div>
          </div>
        )}
        {academicYear && (
          <div className="mt-4">
            <Label>Semester</Label>
            <div className="mt-2">
              {getSemesterOptions().map((semester, index) => (
                <Label key={index} radio className="mr-6">
                  <Input type="radio" value={semester} name="semester" />
                  <span className="ml-2">{semester}</span>
                </Label>
              ))}
            </div>
          </div>
        )}

        <Label className="mt-4">
          <span>Course 1</span>
          <Select className="mt-1">
            <option>Course 1</option>
            <option>Course 2</option>
            <option>Course 3</option>
            <option>Course 4</option>
          </Select>
        </Label>
        <Label className="mt-4">
          <span>Course 2</span>
          <Select className="mt-1">
            <option>Course 5</option>
            <option>Course 6</option>
            <option>Course 7</option>
            <option>Course 8</option>
          </Select>
        </Label>
        <Label className="mt-6" check>
          <Input type="checkbox" />
          <span className="ml-2">
            I agree to the{" "}
            <span className="text-gray-500">
              terms and conditions.
            </span>
          </span>
        </Label>
        <div className="mt-5">
          <Button>Register</Button>
        </div>
      </div>
    </>
  );
}

export default Forms;
