import React, { useState, useEffect } from "react";
import PageTitle from "../components/Typography/PageTitle";
import { Input, Label, Select } from "@windmill/react-ui";
import { Button } from "@windmill/react-ui";

function HonoursMinors() {
  const [honours, setHonours] = useState(""); // New state for honours/minors registration
  const [minors, setMinors] = useState(""); // New state for honours/minors registration

  const [eligibleForHonours, setEligibleForHonours] = useState(false);
  const [eligibleForMinors, setEligibleForMinors] = useState(false);

  const sgpa = sessionStorage.getItem("sgpa");
console.log(sgpa)
  useEffect(() => {
    // Retrieve SGPA and arrears count from session storage
    const arrearsCount = parseInt(sessionStorage.getItem("arrears"));
    // Check eligibility for honours registration
    if (sgpa >= 8.0 && arrearsCount === 0) {
      setEligibleForHonours(true);
    } 
    else if (sgpa >= 6.0 ) {
        setEligibleForMinors(true);
      } 
    else {
      setEligibleForHonours(false);
      setHonours("");
    }
  }, []);

  const handleHonoursMinorsChange = (e) => {
    setHonours(e.target.value);
  };
  const handleMinorsChange = (e) => {
    setMinors(e.target.value);
  };

  const handleSubmit = async () => {
    console.log("Form submitted successfully");
    // Submit the form logic
  };

  return (
    <>
      <PageTitle>Honours/Minors Course Registration</PageTitle>
      <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">
        <div className="mt-4">
          <Label>Honour Registration</Label>
          <div className="mt-2">
            <Select
              className="mt-1"
              onChange={handleHonoursMinorsChange}
              value={honours}
              disabled={!eligibleForHonours} // Disable if not eligible for honours
            >
              <option value="">Select</option>
              <option value="YES">YES</option>
              <option value="NO">NO</option>
            </Select>
          </div>
          {!eligibleForHonours && (
            <p className="mt-2 text-sm text-red-500">
              You are not eligible for honours registration.
            </p>
          )}
        </div>
        {eligibleForHonours && (
          <div className="mt-5">
            <Button onClick={handleSubmit}>Register</Button>
          </div>
        )}
      </div>
      {!eligibleForHonours && (
        <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">
          <Label>Minors Course Selection</Label><div className="mt-2">
            <Select
              className="mt-1"
              onChange={handleMinorsChange}
              value={minors}
              disabled={eligibleForHonours} 
            >
              <option value="">Select</option>
              <option value="YES">YES</option>
              <option value="NO">NO</option>
            </Select>
          </div>
          {eligibleForHonours && (
          <div className="mt-5">
            <Button onClick={handleSubmit}>Register</Button>
          </div>
        )}
        </div>
      )}
    </>
  );
}

export default HonoursMinors;
