import React, { useState } from "react";
import {
  PageTitle,
  Label,
  Input,
  Select,
  Button
} from "@windmill/react-ui";

function Forms() {
  const [academicYear, setAcademicYear] = useState("");
  const [semesters, setSemesters] = useState([]);

  // Define the semesters based on the selected academic year
  const handleAcademicYearChange = (event) => {
    const selectedYear = event.target.value;
    setAcademicYear(selectedYear);
    // Set semesters based on the selected academic year
    if (selectedYear === "2021-2025") {
      setSemesters(["Semester 1", "Semester 2"]);
    } else if (selectedYear === "2022-2026") {
      setSemesters(["Semester 3", "Semester 4"]);
    } else {
      // Handle other academic years if needed
      setSemesters([]);
    }
  };

  return (
    <>
      <PageTitle>Course Registration</PageTitle>
      <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">
        <Label>
          <span>Name</span>
          <Input className="mt-1" placeholder="Jane Doe" />
        </Label>
        <div className="mt-4">
          <Label>Academic year</Label>
          <div className="mt-2">
            <Label radio>
              <Input
                type="radio"
                value="2021-2025"
                name="academicYear"
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2021-2025</span>
            </Label>
            <Label className="ml-6" radio>
              <Input
                type="radio"
                value="2022-2026"
                name="academicYear"
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2022-2026</span>
            </Label>
          </div>
        </div>

        {/* Display semester options based on the selected academic year */}
        {semesters.length > 0 && (
          <Label className="mt-4">
            <span>Semester</span>
            <Select className="mt-1">
              {semesters.map((semester, index) => (
                <option key={index}>{semester}</option>
              ))}
            </Select>
          </Label>
        )}

        <Label className="mt-6" check>
          <Input type="checkbox" />
          <span className="ml-2">
            I agree to the{" "}
            <span className="text-gray-500">
              registered not present in any part of the Academic core subjects.
            </span>
          </span>
        </Label>
        <div className="mt-5">
          <Button>Regular</Button>
        </div>
      </div>
    </>
  );
}

export default Forms;
