import React, { useState } from "react";
import PageTitle from "../components/Typography/PageTitle";
import { Input, Label, Select } from "@windmill/react-ui";
import { Button } from "@windmill/react-ui";

function Forms() {
  const [academicYear, setAcademicYear] = useState(""); // State to manage selected academic year

  const handleAcademicYearChange = (e) => {
    setAcademicYear(e.target.value);
  };

  return (
    <>
      <PageTitle>CourseRegistration</PageTitle>
      <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">
        <Label>
          <span>Name</span>
          <Input className="mt-1" placeholder="Jane Doe" />
        </Label>

        {/* Academic year selection */}
        <div className="mt-4">
          <Label>Academic year</Label>
          <div className="mt-2">
            <Label radio>
              <Input
                type="radio"
                value="2021-2025"
                name="academicYear"
                checked={academicYear === "2021-2025"}
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2021-2025</span>
            </Label>
            <Label className="ml-6" radio>
              <Input
                type="radio"
                value="2022-2026"
                name="academicYear"
                checked={academicYear === "2022-2026"}
                onChange={handleAcademicYearChange}
              />
              <span className="ml-2">2022-2026</span>
            </Label>
          </div>
        </div>

        {/* Semester selection based on academic year */}
        {academicYear && (
          <div className="mt-4">
            <Label>Semester</Label>
            <div className="mt-2">
              <Label radio>
                <Input type="radio" value="Semester 1" name="semester" />
                <span className="ml-2">Semester 1</span>
              </Label>
              <Label className="ml-6" radio>
                <Input type="radio" value="Semester 2" name="semester" />
                <span className="ml-2">Semester 2</span>
              </Label>
            </div>
          </div>
        )}

        <div className="mt-5">
          <Button>Regular</Button>
        </div>
      </div>
    </>
  );
}

export default Forms;
